import React, { Component } from "react";
import Message from "./Message";
import { getMessage } from "./lib/api";
import DisplayTweet from "./Tweet";

class TweetsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: []
    };
    this.getTweetFunc =this.getTweetFunc
  }

  getTweetFunc() {
    getMessage().then(response => {
      // console.log(response);
      console.log(response.data.tweets);
      this.setState({ tweets: response.data.tweets });
    });
  }

  componentDidMount() {
    this.getTweetFunc();
    try {
      setInterval(this.getTweetFunc, 10000);
    } catch (e) {
      console.error(e);
    }
  }

  render() {
    return (
      <div>
        <ul>
          {this.state.tweets.map(tweet => (
            // <Message textMessage={message.textMessage} key={i} />
            <DisplayTweet
              username={tweet.userName}
              date={tweet.date}
              content={tweet.content}
              key={[tweet.date, +"by " + tweet.userName]}
            />
          ))}
        </ul>
      </div>
    );
  }
}
export default TweetsList;
