import React, { Component, Fragment } from "react";

class Form extends Component {
  constructor(props) {
    super(props);
  }

  handleSubmit = event => {
      event.preventDefault();
      console.log()
  }
  render() {
    return (
      <Fragment>
        <form>
          <input 
           type="text"
           placeholder="What you have in mind" 
           maxlength="140"
           onSubmit= {this.handleSubmit}>
          </input>
          <button type="submit">Tweet</button>
        </form>
      </Fragment>
    );
  }
}

export default Form;
