import React, { Component, Fragment } from "react";
import Form from "./components/Form";
import Message from "./components/Message";
import "./App.css";
import TweetsList from "./components/TweetsList";
import { getMessage, postMessage } from "./components/lib/api";
import Profil from "./components/Profil";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link} from 'react-router-dom'

class App extends Component {
  state = {
    messages: []
  };

  addMessage = message => {
    const messages = this.state.messages;
    const textMessage = { textMessage: message };
    messages.unshift(textMessage);
    console.log("message", message);
    this.setState({ messages });
    postMessage(message);
  };

  render() {
    return (
      <div className="App">
        <Router>
          <div>
          <Link to="/about" > About </Link>
          <Link to="/profile" > Profile </Link>
          </div>
          <Switch>
          <Route exact path= "/">
            <h1>Welcome</h1>
          </Route>
          <Route path='/profil/ProductId'>
            <ProductsPage/>
          </Route>
          <Route exact path= "/">
            <h1>Welcome</h1>
          </Route>
          <Route path="/about">
            <CompanyAboutPage />
          </Route>
        </Switch>
        <Form addMessage={this.addMessage} />
        <TweetsList messages={this.state.messages} />
        </Router>
      </div>
    );
  }
}

export default App;
